package main

import (
	"log"

	"github.com/khainv198/go-jwt"
)

func main() {
	clinet, err := jwt.New(&jwt.Options{
		PrivateKey:         "",
		PublicKey:          "",
		SignMethod:         "RS512",
		Issuer:             "ecommerce",
		RefreshTokenExpire: 2592000,
		AccessTokenExpire:  2592000,
	})
	if err != nil {
		log.Fatalf("error new client %v", err)
	}

	r, err := clinet.GenerateAccessToken(jwt.JwtReq{ID: nil})
	if err != nil {
		log.Fatalf("generate access token error %v", err)
	}

	log.Print(r.Token)
}
